CREATE TABLE "comments" ("cid" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL ,"objid" INT  NOT NULL ,"objtype" SMALLINT  NOT NULL DEFAULT 0,"content" VARCHAR(1024)  NOT NULL ,"uid" INT  NOT NULL ,"floor" INT  NOT NULL ,"flag" SMALLINT  NOT NULL DEFAULT 0,"ctime" TIMESTAMP  NOT NULL );
CREATE TABLE sqlite_sequence(name,seq);
CREATE TABLE "message" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL ,"content" TEXT  NOT NULL ,"hasread" NVARCHAR(0)  NOT NULL DEFAULT 'Î´¶Á',"from" INT  NOT NULL DEFAULT 0,"fdel" NVARCHAR(0)  NOT NULL DEFAULT 'Î´É¾',"to" INT  NOT NULL ,"tdel" NVARCHAR(0)  NOT NULL DEFAULT 'Î´É¾',"ctime" TIMESTAMP  NOT NULL );
