CREATE TABLE "topics" ("tid" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL ,"title" VARCHAR(255)  NOT NULL ,"content" VARCHAR(1024)  NOT NULL ,"nid" INT  NOT NULL ,"uid" INT  NOT NULL ,"lastreplyuid" INT  NOT NULL DEFAULT 0,"lastreplytime" TIMESTAMP  NOT NULL ,"view" INT  NOT NULL DEFAULT 0,"reply" INT  NOT NULL DEFAULT 0,"like" INT  NOT NULL DEFAULT 0,"hate" INT  NOT NULL DEFAULT 0,"flag" SMALLINT  NOT NULL DEFAULT 0,"ctime" TIMESTAMP  NOT NULL ,"mtime" TIMESTAMP  NOT NULL );
